import React from 'react';
import {AddText} from "../../../common/addText/AddText";
import {MyPost} from "./myPost/MyPost";

import {Paper} from "@material-ui/core";
import style from '../../../common/addText/addText.module.css'
import {PostsType} from "../../../redux/profileReducer";
import {Textarea} from "../../../common/textarea/Textarea";

type PostPageType = {
    posts: Array<PostsType>
    newPostText: string
    photoUser: string | null
    addPostState: () => void
    newTextPost: (newText: string) => void
}
export const MyPosts = (props: PostPageType) => {
    function addPost() {
        props.addPostState()
        props.newTextPost('')
    }

    function onChangeNewTextHandler(newText: string) {
        props.newTextPost(newText)
    }

    let newPost = props.posts.map(item => <MyPost key={item.id} massage={item.massage} likes={item.likes}
                                                  photoUser={props.photoUser}/>)
    return (
        <Paper elevation={3} className={style.postBloc}>
            <h2>Мои посты</h2>
            <Textarea text={props.newPostText}
                      add={addPost}
                      onChangeHandler={onChangeNewTextHandler}
            />
            {newPost}
        </Paper>
    );
};
