import style from '../userInfo.module.css';
import {PhotoCamera} from '@material-ui/icons';

type AvatarParams = {
    src: string
};
export const AvatarUser = (props: AvatarParams) => {

    const onChangeHandler = (e: ChangeEvent<HTMLInputElement>) => {
