import React, {useEffect, useState} from 'react';
import CreateIcon from '@material-ui/icons/Create';
import style from '../users/users.module.css';
import TextField from '@material-ui/core/TextField/TextField';
import IconButton from '@material-ui/core/IconButton/IconButton';
import {makeStyles} from '@material-ui/core/styles';

type EditModeParams = {
    value: string | null
    isOwner: boolean
    label: string
    callback: (value: string) => void
};
export const EditMode = (props: EditModeParams) => {
    const styles = makeStyles(theme => {
        textField:{
            // eslint-disable-next-line @typescript-eslint/no-unused-expressions
            border: '1px solid read'
        }
    })


    const [valueInput, setValueInput] = useState(props.value)
    const [editMode, setEditMode] = useState(true)

    function onClickHandler() {
        setEditMode(true)
    }

    function onBlurHandler() {

        if (valueInput !== props.value) {
            valueInput && props.callback(valueInput)
        }
        // setEditMode(false)
    }

    useEffect(() => {
        setValueInput(props.value)
    }, [props.value])

    return (
        <div className={style.editBlock}>
            {props.isOwner
                ? <>
                    {editMode
                        ? <TextField
                            id={props.label}
                            multiline
                            autoFocus={true}
                            maxRows={4}
                            inputProps={{maxLength: 80}}
                            value={valueInput}
                            onBlur={onBlurHandler}
                            color={'primary'}
                            onFocus={(e) =>
                                e.currentTarget.setSelectionRange(
                                    e.currentTarget.value.length,
                                    e.currentTarget.value.length
                                )}
                            className={styles}
                            onChange={e => setValueInput(e.currentTarget.value)}
                        />

                        : <>
                            <SpanText value={props.value}
                                      className={style.textStatus}/>

                            <IconButton size={'small'} disabled={editMode} onClick={onClickHandler}>
                                <CreateIcon className={style.icon}/>
                            </IconButton>
                        </>
                    }
                </>
                : <SpanText value={props.value}/>
            }
        </div>
    );
};

type SpanTextType = {
    value: string | null
    className?: string
    callback?: () => void
}
const SpanText = (props: SpanTextType) => {
    return <span className={props.className}
                 onDoubleClick={props.callback}>
            {props.value || 'нету даных'}
        </span>
}
