import React, {useEffect} from 'react';
import {MyPosts} from '../components/profile/myPosts/MyPosts';
import {connect} from 'react-redux';
import {AppStoreType,} from '../redux/store';
import UserInfo from '../components/profile/userInfo/UserInfo';
import {Loading} from '../components/common/loading/Loading';
import {useParams} from 'react-router';
import {useNavigate} from 'react-router-dom';
import {
    addPost,
    fetchStatusUpdates,
    fetchUserProfileData,
    PostsType,
} from '../redux/profileReducer';
import {StatusAuthorizedType} from '../redux/appReducer';
import {AuthorizedUserType, UserProfileType} from '../api/api';
import {useAppDispatch, useAppSelector} from '../utils/hooks/hooks';


type MapDispatchToPropsType = {
    addPost: (value: string) => void
    fetchUserProfileData: (paramsURL: number) => void
    fetchStatusUpdates: (newStatus: string, userId: number) => void
}


const Profile = () => {
    const dispatch = useAppDispatch()

    const {authorizedStatus, authorizedUser, isLoading, posts, userProfile, userStatus} = useAppSelector(state =>
        ({
            authorizedStatus: state.app.authorizedStatus,
            authorizedUser: state.app.authorizedUser,
            isLoading: state.app.isLoading,
            posts: state.profilePage.posts,
            userProfile: state.profilePage.userProfile,
            userStatus: state.profilePage.userStatus,
        }))

    let params = useParams<'*'>()
    let paramsURL = Number(params['*'])
    const navigate = useNavigate()
    useEffect(() => {
        if (authorizedStatus === 'successfully' && !params['*']) {
            if (authorizedUser.id) paramsURL = authorizedUser.id
        }
        if (authorizedStatus === 'successfully' || paramsURL) {
            fetchUserProfileData(paramsURL)
        } else if (authorizedStatus === 'fail') {
            navigate('/login')
        }

    }, [authorizedStatus, paramsURL])

    if (Object.keys(userProfile).length === 0) {
        return <Loading/>
    }
    return (
        <>
            <UserInfo userProfile={userProfile}
                      authorizedUserId={authorizedUser.id}
                      fetchStatusUpdates={fetchStatusUpdates}
                      userStatus={userStatus}/>
            <MyPosts photoUser={userProfile.photos.small}
                     posts={posts}
                     addPost={addPost}
            />
        </>);
};


export default connect<MapStateToPropsType, MapDispatchToPropsType, {}, AppStoreType>(mapStateToProps, {
    addPost,
    fetchUserProfileData,
    fetchStatusUpdates
})(Profile);
